import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Home, Library, Plus, Brain, ClipboardCheck, Sparkles } from "lucide-react";

const navigationItems = [
  { title: "Home", url: "/home", icon: Home },
  { title: "Dashboard", url: "/dashboard", icon: Library },
  { title: "Create", url: "/create", icon: Plus },
  { title: "Study", url: "/study", icon: Brain },
  { title: "Test", url: "/test", icon: ClipboardCheck },
];

export default function Layout({ children }) {
  const location = useLocation();
  const showSidebar = location.pathname !== '/home';

  if (!showSidebar) {
    return <main data-filename="pages/CodeExporter" data-linenumber="127" data-visual-selector-id="pages/CodeExporter127">{children}</main>;
  }

  return (
    <div data-filename="pages/CodeExporter" data-linenumber="131" data-visual-selector-id="pages/CodeExporter131" className="min-h-screen flex">
      <aside data-filename="pages/CodeExporter" data-linenumber="132" data-visual-selector-id="pages/CodeExporter132" className="w-64 bg-slate-800 text-white flex flex-col">
        <div data-filename="pages/CodeExporter" data-linenumber="133" data-visual-selector-id="pages/CodeExporter133" className="p-6 flex items-center gap-3 border-b border-slate-700">
          <div data-filename="pages/CodeExporter" data-linenumber="134" data-visual-selector-id="pages/CodeExporter134" className="w-10 h-10 bg-indigo-600 rounded-lg flex items-center justify-center">
            <Sparkles data-filename="pages/CodeExporter" data-linenumber="135" data-visual-selector-id="pages/CodeExporter135" className="w-6 h-6" />
          </div>
          <h1 className="font-bold text-xl">FlashGenius</h1>
        </div>
        <nav data-filename="pages/CodeExporter" data-linenumber="139" data-visual-selector-id="pages/CodeExporter139" className="flex-1 p-4">
          <ul data-filename="pages/CodeExporter" data-linenumber="140" data-visual-selector-id="pages/CodeExporter140">
            {navigationItems.map(item => (
              <li data-filename="pages/CodeExporter" data-linenumber="142" data-visual-selector-id="pages/CodeExporter142" key={item.title}>
